					<?  $roles=get_role_names();
						foreach ($roles as $role=>$lang):
						if($role !='administrator')
						{
							$checked = get_option('chatwee-settings-group[group_moderators_'.$role.']');
							$checked1 = $checked == 'on' ? 'checked="checked"' : '';
							echo '<div class="right">';
							echo '<input '.$checked1.' id="group_moderators_'.$role.'" name="group_moderators_'.$role.'" type="checkbox" />';
							echo '<label for="group_moderators_'.$role.'">'.$lang.'</label>';
							echo '</div>';
						}
					?>
					<? endforeach;?>

					<p class="option-info">This section allows you to turn all users assigned to the particular role in WordPress into moderators by marking a single checkbox.</p>
						

					<input type="submit" name="chatweesubmit" class="button-primary" value="Save changes" />

				
				</form>
			
			</article>
			
			<article>
			
