					<tbody class="allModerators">
					
						<?foreach ($mods as $mod) {
								$mod_info = get_userdata($mod->user_id);
								$row ='';
								$row.= '<tr><td>';
								$row.= $mod->user_id.'</td><td>';
								$row.= $mod_info->user_login.'</td><td>';
								$row.= $mod_info->display_name.'</td><td>';
